import React, {useState} from 'react';

const UserForm = () => {

    const [ data, setData ] = useState({
        name: '',
        age: 0
    });

    const { name, age } = data;

    const handlerInfoChange = (e) => {
        setData({
            ...data,
            [e.target.name] : e.target.value
        })   
    }

    const addNewUser = (e) => {
        e.preventDefault()

        if( name.trim() === '' || age < 0){
            console.log('campos no pueden ir vacios')
        }
    }

    return (  
        <>
            <div>
                <form
                    onSubmit={ addNewUser }
                >
                    <div>
                        <label htmlFor="name">Name</label>

                        <input
                            type="text"
                            placeholder="Enter a name"
                            id="name"
                            name="name"
                            onChage={handlerInfoChange}
                            value={name}
                        />
                    </div>
                    <div>
                        <label htmlFor="age">Age</label>

                        <input
                            type="number"
                            min="0"
                            id="age"
                            name="age"
                            onChage={handlerInfoChange}
                            value={age}
                        />
                    </div>

                    <div>
                        <button

                        >Add user</button>
                    </div>
                </form>
            </div>
        </>
    );
}

export default UserForm;
